import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = "http://127.0.0.1:5000";

function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const [newEmp, setNewEmp] = useState({ name:"", email:"", role:"" });
  const [editId, setEditId] = useState(null);

  const token = localStorage.getItem("token");

  const fetchEmployees = async () => {
    try {
      const res = await axios.get(`${API_URL}/employees`, { headers: { Authorization: `Bearer ${token}` } });
      setEmployees(res.data);
    } catch(err) {
      alert("Failed to fetch employees");
    }
  };

  useEffect(() => { fetchEmployees(); }, []);

  const addEmployee = async () => {
    await axios.post(`${API_URL}/employees`, newEmp, { headers: { Authorization: `Bearer ${token}` } });
    setNewEmp({ name:"", email:"", role:"" });
    fetchEmployees();
  };

  const updateEmployee = async (id) => {
    await axios.put(`${API_URL}/employees/${id}`, newEmp, { headers: { Authorization: `Bearer ${token}` } });
    setNewEmp({ name:"", email:"", role:"" });
    setEditId(null);
    fetchEmployees();
  };

  const deleteEmployee = async (id) => {
    await axios.delete(`${API_URL}/employees/${id}`, { headers: { Authorization: `Bearer ${token}` } });
    fetchEmployees();
  };

  return (
    <div style={{ margin: "50px" }}>
      <h2>Employee List</h2>
      <input placeholder="Name" value={newEmp.name} onChange={e=>setNewEmp({...newEmp, name:e.target.value})}/>
      <input placeholder="Email" value={newEmp.email} onChange={e=>setNewEmp({...newEmp, email:e.target.value})}/>
      <input placeholder="Role" value={newEmp.role} onChange={e=>setNewEmp({...newEmp, role:e.target.value})}/>
      {editId ? 
        <button onClick={()=>updateEmployee(editId)}>Update Employee</button> : 
        <button onClick={addEmployee}>Add Employee</button>}
      <table border="1" cellPadding="10" style={{marginTop:"20px"}}>
        <thead>
          <tr><th>ID</th><th>Name</th><th>Email</th><th>Role</th><th>Actions</th></tr>
        </thead>
        <tbody>
          {employees.map(emp=>(
            <tr key={emp.id}>
              <td>{emp.id}</td>
              <td>{emp.name}</td>
              <td>{emp.email}</td>
              <td>{emp.role}</td>
              <td>
                <button onClick={()=>{setEditId(emp.id); setNewEmp({name:emp.name,email:emp.email,role:emp.role})}}>Edit</button>
                <button onClick={()=>deleteEmployee(emp.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EmployeeList;
